{
  "id": "foliage-test",
  "name": "A Foliage Test Recipe",
  "description": "Greenery",
  "recipeType": "partitioned-workflow",
  "inputType": "seasonal-acquisition",
  "outputType": "vector-service",
  "properties": {
    "image_bands": "Pan_MS1_MS2"
  },
  "definition": {
    "name": "Foliage test (full workflow)",
    "tasks": [
      {
        "name": "aop",
        "taskType": "AOP_Strip_Processor",
        "inputs": [
          {
            "name": "enable_pansharpen",
            "value": "false"
          },
          {
            "name": "enable_dra",
            "value": "false"
          },
          {
            "name": "bands",
            "value": "MS"
          },
          {
            "name": "data",
            "value": "{raster_path}"
          },
          {
            "name": "ortho_epsg",
            "value": "UTM"
          },
          {
            "name": "ortho_interpolation_type",
            "value": "Bilinear"
          }
        ],
        "outputs": [
          {
            "name": "data"
          },
          {
            "name": "log"
          }
        ]
      },
      {
        "taskType": "AOP_ENVI_HDR",
        "name": "hdr",
        "inputs": [
          {
            "name": "image",
            "source": "aop:data"
          }
        ],
        "outputs": [
          {
            "name": "output_data"
          }
        ]
      },
      {
        "taskType": "ENVI_SpectralIndex",
        "name": "ndvi",
        "inputs": [
          {
            "name": "index",
            "value": "Leaf Area Index"
          },
          {
            "name": "input_raster",
            "source": "hdr:output_data"
          }
        ],
        "outputs": [
          {
            "name": "output_raster_uri"
          },
          {
            "name": "task_meta_data"
          }
        ]
      },
      {
        "taskType": "ENVI_ColorSliceClassification",
        "name": "range",
        "inputs": [
          {
            "name": "input_raster",
            "source": "ndvi:output_raster_uri"
          },
          {
            "name": "class_ranges",
            "value": "[4.51, 10.01]"
          },
          {
            "name": "class_colors",
            "value": "[0, 255, 0]"
          }
        ],
        "outputs": [
          {
            "name": "output_raster_uri"
          }
        ]
      },
      {
        "taskType": "ENVI_ClassificationSmoothing",
        "name": "smoothing",
        "inputs": [
          {
            "name": "input_raster",
            "source": "range:output_raster_uri"
          }
        ],
        "outputs": [
          {
            "name": "output_raster_uri"
          },
          {
            "name": "task_meta_data"
          }
        ]
      },
      {
        "taskType": "ENVI_ClassificationAggregation",
        "name": "aggregation",
        "inputs": [
          {
            "name": "input_raster",
            "source": "smoothing:output_raster_uri"
          }
        ],
        "outputs": [
          {
            "name": "output_raster_uri"
          },
          {
            "name": "task_meta_data"
          }
        ]
      },
      {
        "taskType": "ENVI_ClassificationToShapefile",
        "name": "shapefile",
        "timeout": 36000,
        "inputs": [
          {
            "name": "input_raster",
            "source": "aggregation:output_raster_uri"
          },
          {
            "name": "export_classes",
            "value": "4.51 to 10.01"
          }
        ],
        "outputs": [
          {
            "name": "output_vector_uri"
          },
          {
            "name": "task_meta_data"
          }
        ]
      },
      {
        "taskType": "simplify-polygon:0.0.9",
        "name": "simplify",
        "inputs": [
          {
            "name": "shapefile",
            "source": "shapefile:output_vector_uri"
          },
          {
            "name": "tolerance",
            "value": "2.0"
          },
          {
            "name": "min_area",
            "value": "100.0"
          }
        ],
        "outputs": [
          {
            "name": "output_shapefile"
          }
        ]
      },
      {
        "name": "reproject",
        "taskType": "gdal-cli-multiplex",
        "timeout": 36000,
        "inputs": [
          {
            "name": "data",
            "source": "simplify:output_shapefile"
          },
          {
            "name": "command",
            "value": "mkdir -p $outdir/data; ogr2ogr -f \"ESRI Shapefile\" -a_srs \"EPSG:4326\" -t_srs \"EPSG:4326\" $outdir/data/reprojected.shp $indir/data/*.shp"
          }
        ],
        "outputs": [
          {
            "name": "data"
          }
        ]
      },
      {
        "taskType": "IngestShpToVectorServices",
        "name": "vector",
        "timeout": 36000,
        "impersonation_allowed": true,
        "containerDescriptors": [ { "properties": { "domain": "t2medium" } } ],
        "inputs": [
          {
            "name": "items",
            "source": "reproject:data"
          },
          {
            "name": "index",
            "value": "vector-foliage-{ingest_date}"
          },
          {
            "name": "shapefileMapping",
            "value": "vector.crs=EPSG:4326\nvector.ingestSource=Foliage Detection Testing\nvector.itemType=Foliage"
          }
        ],
        "outputs": [
          {
            "name": "result"
          }
        ]
      },
      {
        "name": "s3_vector",
        "taskType": "StageDataToS3",
        "timeout": 36000,
        "containerDescriptors": [ { "properties": { "domain": "t2medium" } } ],
        "inputs": [
          {
            "name": "data",
            "source": "vector:result"
          },
          {
            "name": "destination",
            "value": "s3://{vector_ingest_bucket}/{recipe_id}/{run_id}/{task_name}"
          }
        ],
        "outputs": []
      }
    ]
  }
}

